export const count = ({ values }) => {
    return values.length;
};
export const sum = ({ values }) => {
    if (!values.length) {
        return null;
    }
    return values.reduce((total, current) => total + ((typeof current === 'number' && isFinite(current))
        ? current
        : 0), 0);
};
export const avg = ({ column, values }) => {
    if (!values.length) {
        return null;
    }
    const total = sum({ column, values });
    return total / values.length;
};
export const max = ({ values }) => {
    if (!values.length) {
        return null;
    }
    let maximum = null;
    values.forEach(value => {
        if (maximum === null
            || (value !== null && value > maximum)) {
            maximum = value;
        }
    });
    return maximum;
};
export const min = ({ values }) => {
    if (!values.length) {
        return null;
    }
    let minimum = null;
    values.forEach(value => {
        if (minimum === null
            || (value !== null && value < minimum)) {
            minimum = value;
        }
    });
    return minimum;
};
