import { type TableData, type GroupedData, type OrderColumn } from '../types';
export type OrderPipe = (param: {
    orderBy?: OrderColumn[];
    source: TableData;
}) => (tableData: TableData) => TableData;
export type OrderGroupPipe = (param: {
    orderBy?: OrderColumn[];
    source: TableData;
}) => (grouped: GroupedData) => GroupedData;
export declare const order: OrderPipe;
export declare const orderGroup: OrderGroupPipe;
