import { DEFAULT_CONTINUOUS_LEGEND_THEME } from "./continuous";

const getHandlerTheme = align => ({
    style: {
        symbolType: getSizeHandlerPath(align),
        fill: "#fff",
        lineWidth: 1,
        size: 10,
        stroke: "#ccc",
        outerBorder: !1
    }
});

export const sizeLegend = {
    horizontal: Object.assign(Object.assign({}, DEFAULT_CONTINUOUS_LEGEND_THEME), {
        sizeBackground: {
            fill: "#cdcdcd"
        },
        track: {
            style: {
                fill: "rgba(20,20,20,0.1)"
            }
        },
        rail: {
            width: 200,
            height: 4,
            style: {
                fill: "rgba(0,0,0,0.04)"
            }
        },
        handler: getHandlerTheme("top")
    }),
    vertical: Object.assign(Object.assign({}, DEFAULT_CONTINUOUS_LEGEND_THEME), {
        sizeBackground: {
            fill: "#cdcdcd"
        },
        track: {
            style: {
                fill: "rgba(20,20,20,0.1)"
            }
        },
        rail: {
            width: 4,
            height: 200,
            style: {
                fill: "rgba(0,0,0,0.04)"
            }
        },
        handler: getHandlerTheme("right")
    })
};

export function getSizeHandlerPath(align = "bottom") {
    let centerX = 0;
    return "top" === align ? `\n    M${centerX},-6L${centerX - 3.5},-2.5\n    v5\n    h7\n    v-5\n    Z\n` : "left" === align ? (centerX = 1, 
    `\n    M${centerX - 6},0L${centerX - 6 + 2.5},-3.5\n    h5\n    v7\n    h-5\n    Z\n`) : "right" === align ? (centerX = -1, 
    `\n    M${centerX + 6},0L${centerX + 6 - 2.5},-3.5\n    h-5\n    v7\n    h5\n    Z\n  `) : `\n    M${centerX},6L${centerX - 3.5},2.5\n    v-5\n    h7\n    v5\n    Z\n`;
}
